import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.util.Iterator;
import java.lang.String
import org.apache.poi.hssf.usermodel.HSSFCell;
import org.apache.poi.hssf.usermodel.HSSFRow;
import org.apache.poi.hssf.usermodel.HSSFSheet;
import org.apache.poi.hssf.usermodel.HSSFWorkbook;
import org.apache.poi.xssf.usermodel.XSSFCell;
import org.apache.poi.xssf.usermodel.XSSFRow;
import org.apache.poi.xssf.usermodel.XSSFSheet;
import org.apache.poi.xssf.usermodel.XSSFWorkbook;


def TestStep_Count = testRunner.testCase.getTestStepCount()
def DataSource_Flag
def RunTimeDataSource_Flag
log.info TestStep_Count
def TestStep_Name
//******************Finding DataSource***************
for(i = 0;i < TestStep_Count;i++){
	TestStep_Name = testRunner.testCase.getTestStepAt(i).name
	log.info TestStep_Name
	if(TestStep_Name == "DataSource"){
		log.info("Data Source present")
		DataSource_Flag = true
		break
	}else{
		DataSource_Flag = false
	}
}

for(i = 0; i < TestStep_Count; i++){
	TestStep_Name = testRunner.testCase.getTestStepAt(i).name
	if(TestStep_Name == "DataSource_RunTime"){
		log.info("RunTime Data Source present")
		RunTimeDataSource_Flag = true
		break
	}
	else{
		RunTimeDataSource_Flag = false
		log.info("RunTime Data Source not present")
	}
}
log.info "Data Source: " + DataSource_Flag
log.info "Run Time Data Source: " + RunTimeDataSource_Flag


//******************Deleting Properties from DataSource****************
if(DataSource_Flag == true){
	log.info "Deleting DataSource Properties"
	def TestStep = testRunner.testCase.getTestStepByName("DataSource")
	def Property_Count = TestStep.propertyCount
	log.info Property_Count
	def Property_Name
	for(i = Property_Count - 1; i >= 0; i--)
	{
		Property_Name = TestStep.getPropertyAt(i).name
		log.info "Deleting: " + Property_Name
		TestStep.removeProperty(Property_Name)
	}
}else{
	log.info("Data Source not present")
}

//******************Deleting Properties from Run Time DataSource****************
if(RunTimeDataSource_Flag == true){
	log.info "Deleting RunTime DataSource Properties"
	def TestStep = testRunner.testCase.getTestStepByName("DataSource_RunTime")
	def Property_Count = TestStep.propertyCount
	log.info Property_Count
	def Property_Name
	for(i = Property_Count - 1; i >= 0; i--)
	{
		Property_Name = TestStep.getPropertyAt(i).name
		log.info "Deleting: " + Property_Name
		TestStep.removeProperty(Property_Name)
	}
}else{
	log.info("RunTime Data Source not present")
}

//*********Reading Excel and adding Property to DataSource*****************

if(DataSource_Flag == true){
	def Test_Data_Path = context.expand( '${Test_Data_Path}' )
	InputStream ExcelFileToRead = new FileInputStream(Test_Data_Path);
	XSSFWorkbook wb = new XSSFWorkbook(ExcelFileToRead);
	XSSFSheet sheet = wb.getSheet("Data")
	XSSFRow row; 
	XSSFCell cell;

	row = sheet.getRow(0)
	def Col_count = row.getLastCellNum()
	log.info Col_count

	log.info "Adding DataSource Properties"
	def TestStep = testRunner.testCase.getTestStepByName("DataSource")
	def Property_Name
	for(i = 0; i < Col_count; i++){
		cell = row.getCell(i)
		def Cell_Value = String.valueOf(cell)
		log.info "Adding: " + Cell_Value
		TestStep.addProperty(Cell_Value)
	}
}


//**************Reading Excel and adding Property to RunTime DataSource***********
if(RunTimeDataSource_Flag == true){
	def Run_Time_Data = context.expand( '${Run_Time_Data}' )
	InputStream ExcelFileToRead = new FileInputStream(Run_Time_Data);
	HSSFWorkbook wb = new HSSFWorkbook(ExcelFileToRead);
	HSSFSheet sheet = wb.getSheet("Account_Run_Time_Data")
	HSSFRow row; 
	HSSFCell cell;

	row = sheet.getRow(0)
	def Col_count = row.getLastCellNum()
	log.info Col_count

	log.info "Adding DataSource Properties"
	def TestStep = testRunner.testCase.getTestStepByName("DataSource_RunTime")
	def Property_Name
	for(i = 0; i < Col_count; i++){
		cell = row.getCell(i)
		def Cell_Value = String.valueOf(cell)
		log.info "Adding: " + Cell_Value
		TestStep.addProperty(Cell_Value)
	}
}

def dataCombination = context.expand( '${DataSource#Data_Combination}' )
log.info dataCombination
def API_Name = context.expand( '${DataSource#API_Name}' )
def TestCaseName = testRunner.testCase.getName()

if (API_Name != TestCaseName){
	testRunner.testCase.testSteps['outofThreshold'].setDisabled(true)
	testRunner.testCase.testSteps['Report'].setDisabled(true)
}else{
	testRunner.testCase.testSteps['outofThreshold'].setDisabled(false)
	testRunner.testCase.testSteps['Report'].setDisabled(false)

}

import java.io.FileInputStream import java.io.File import java.io.FileNotFoundException import java.io.FileOutputStream import java.io.IOException import java.util.Iterator import java.io.InputStream import java.lang.String import java.text.SimpleDateFormat import org.apache.poi.EncryptedDocumentException import org.apache.poi.openxml4j.exceptions.InvalidFormatException import org.apache.poi.ss.util.CellReference import org.apache.poi.ss.usermodel.Cell import org.apache.poi.ss.usermodel.DateUtil import org.apache.poi.ss.usermodel.Row import org.apache.poi.ss.usermodel.Sheet import org.apache.poi.ss.usermodel.Workbook import org.apache.poi.ss.usermodel.WorkbookFactory import org.apache.poi.hssf.usermodel.HSSFWorkbook import org.apache.poi.xssf.usermodel.XSSFWorkbook import org.apache.poi.xssf.streaming.SXSSFWorkbook import org.apache.poi.xssf.usermodel.XSSFCell import org.apache.poi.xssf.usermodel.XSSFRow import org.apache.poi.xssf.usermodel.XSSFSheet import org.apache.poi.xssf.usermodel.XSSFWorkbook
def Final_Path = com.eviware.soapui.SoapUI.globalProperties.getPropertyValue("Test_Result_Path") log.info Final_Path def final_path = new File(Final_Path) final_path.mkdir()
/*Get Test Case Name and Status*/ def data_combination = context.expand( '${DataSource#Data_Combination}' ) /*Get TC Name*/ def TCN = context.expand( '${DataSource#TC_Number}' ) def TestCaseName = testRunner.testCase.getName()
log.info TestCaseName
obj = context.testCase.getTestStepByName(TestCaseName) assertions = obj.getAssertionList() def Status assertions.each{ def stat = (it.status).toString() if(stat == "VALID" || stat == "PASS"){ Status = "Pass" } if(stat == "FAILED" || stat == "FAIL"){ Status = "Fail" } } log.info(Status) testRunner.testCase.testSuite.setPropertyValue("Status", Status)
/*Creating Txt File*/
String date = new Date() String date_now = new Date() date = date.replaceAll(":","_") date = date.replaceAll(" ","_") def TCName = TestCaseName.replaceAll(" ","_") report_file = Final_Path + TCN + "_" + TCName + "_" + date + "_" + Status + ".txt" /* Writing to file */ def file1 = new File(report_file)
/*Get Time Taken for request*/
obj = context.testCase.getTestStepByName(TestCaseName) assertions = obj.getAssertionList() def Time_Taken assertions.each{ Time_Taken = testRunner.testCase.testSteps[TestCaseName].testRequest.response.timeTaken log.info(Time_Taken) }
/*Get Status Code*/ Header = testRunner.testCase.testSteps[TestCaseName].testRequest.response.getResponseHeaders() Status_Code = Header["#status#"] log.info Status_Code file1.write("Status: " + Status + "
") file1.append("Time Taken: " + Time_Taken + " ms
") file1.append("TimeStamp: " + date_now + "
") file1.append("Test Step: " + TestCaseName + "



") file1.append("----------------- Http Status ------------------------------
") file1.append("Http Status Code: " + Status_Code + "



") file1.append("---------------- Request ---------------------------
")
/*Get request and response*/ try{ myRequestStep = testRunner.testCase.getTestStepByName(TestCaseName) def request = new String(myRequestStep.testRequest.messageExchange.rawRequestData) def response = new String(myRequestStep.testRequest.messageExchange.rawResponseData) file1.append(request + "



") file1.append("---------------- Response --------------------------
") file1.append(response) } catch (Exception e){ log.info("Empty Request Response") }
/* Creating Excel for test result */ def Testresultpath = com.eviware.soapui.SoapUI.globalProperties.getPropertyValue("Test_Result_Path") Testresultpath = Testresultpath + "Test_Run_Results.xls" log.info Testresultpath
FileInputStream fis = new FileInputStream(new File(Testresultpath)) Workbook wb = WorkbookFactory.create(fis) Sheet sh = wb.getSheet("Test_Run_Results") def Row_count = sh.getLastRowNum() Row_count = Row_count.toInteger() Row_count++ log.info Row_count Row row = sh.createRow(Row_count) Cell cell = row.createCell(0).setCellValue(TCN) Cell cell_1 = row.createCell(1).setCellValue(Status) Cell cell_2 = row.createCell(2).setCellValue(TestCaseName) Cell cell_3 = row.createCell(3).setCellValue(date_now) Cell cell_4 = row.createCell(4).setCellValue(Time_Taken) FileOutputStream out = new FileOutputStream(Testresultpath,false) wb.write(out) out.close()
